<?phpfunction MoneyStrToInt($v_str){	$return_number = 0;	$v_str = str_replace("$", "", $v_str);	$v_str = str_replace(",", "", $v_str);		if(substr_count($v_str, "K") > 0)	{		$v_str = str_replace("K", "", $v_str);		$return_number = floatval($v_str);		$return_number *= 1000;		$return_number += 500;	}	else if(substr_count($v_str, " mil") > 0)	{		$v_str = str_replace(" mil", "", $v_str);		$return_number = floatval($v_str);		$return_number *= 1000000;	}	else	{		$return_number = floatval($v_str);	}		return intval($return_number);}function MoneyIntToStr($v_int){	$strlen = strlen($v_int);	$str = "";	$counter = 0;		for($i = 1; $i < $strlen / 3; $i++)	{		$str = ",".substr($v_int, ($i * -3), 3) . $str;		$counter += 3;	}	$str = "$".substr($v_int, 0, $strlen - $counter) . $str;		return $str;}?>